import random

def simple(A):
  """
  Generate simple graph from graph represented by adjacency matrix A.
  Function returns adjacency matrix S of simple graph.
  """
  S = []
  for a in A:
    S.append([1 if i > 0 else 0 for i in a])
  return S

def gnp(n, p):
  """
  Generate Erdos-Renyi random graph with n nodes and edge probability p.
  Function returns triple consisting of graph name `G(n,p)`, adjacency matrix A and node labels set to None.
  """
  A = [[0 for _ in range(n)] for _ in range(n)]
  for i in range(n):
    for j in range(i + 1, n):
      if random.random() < p:
        A[i][j] = 1
        A[j][i] = 1
  return ('G(n,p)', A, None)

def gnm(n, m):
  """
  Generate Erdos-Renyi random graph with n nodes and m edges.
  Function returns triple consisting of graph name `G(n,m)`, adjacency matrix A and node labels set to None.
  """
  A = [[0 for _ in range(n)] for _ in range(n)]
  for _ in range(m):
    i = random.randint(0, n - 1)
    j = random.randint(0, n - 1)
    A[i][j] += 1
    A[j][i] += 1
  return ('G(n,m)', A, None)

def component(A, N, i):
  """
  Find connected component of node i in graph represented by adjacency matrix A considering only nodes in list N.
  Function returns list of nodes C in connected component of node i and removes them from list N.
  """
  C = []
  S = []
  N.remove(i)
  S.append(i)
  while S:
    i = S.pop(0)
    C.append(i)
    for j in range(len(A)):
      if A[i][j] and j in N:
        N.remove(j)
        S.insert(0, j)
  return C

def components(A):
  """
  Find connected components of graph represented by adjacency matrix A.
  Function returns list of lists C of nodes in connected components of graph.
  """
  C = []
  N = set(range(len(A)))
  while N:
    C.append(component(A, N, next(iter(N))))
  return C

def info(G):
  """
  Print out standard information of graph represented by adjacency matrix A.
  Method prints out graph name and type, number of nodes and edges, average node degree, and size and number of connected components.
  """
  (name, A, L) = G
  S = simple(A)
  n = len(A)
  k = [sum(a) for a in A]
  m = int(sum(k) / 2)
  C = components(A)

  print("{:>12s} | '{:s}'".format('Graph', name))
  print("{:>12s} | '{:s}'".format('Type', ('x' if L is not None else '0') + ('--' if A == S else '==') + ('y' if L is not None else '1')))
  print("{:>12s} | {:,d}".format('Nodes', n))
  print("{:>12s} | {:,d}".format('Edges', m))
  print("{:>12s} | {:.2f}".format('Degree', 2 * m / n))
  print("{:>12s} | {:.1f}% ({:,d})".format('LCC', 100 * max([len(c) for c in C]) / n, len(C)))
  print()

