# prints out standard information of demo graph G

info(G)

# defines numbers of nodes N and edges M for random graphs

N = 1000
M = 2500

# prints out standard information of different random graphs

info(gnp(N, 2 * M / N / (N - 1)))
info(gnm(N, M))

